var Login = function () {
    var e = function () {
        $(".login-form").validate({
            errorElement: "span",
            errorClass: "help-block",
            focusInvalid: !1,
            rules: {
                username: {
                    required: !0
                },
                password: {
                    required: !0
                },
                tnc: {
                    required: !0
                }
            },
            messages: {
                username: {
                    required: "Username is required."
                },
                password: {
                    required: "Password is required."
                }
            },
            invalidHandler: function () {
                $(".alert-danger", $(".login-form")).show()
            },
            highlight: function (e) {
                $(e).closest(".form-group").addClass("has-error")
            },
            success: function (e) {
                e.closest(".form-group").removeClass("has-error"), e.remove()
            },
            errorPlacement: function (e, r) {
                e.insertAfter(r.closest(".input-icon"))
            },
            submitHandler: function (e) {
                e.submit();
            }
        }), $(".login-form input").keypress(function (e) {
            return 13 == e.which ? ($(".login-form").validate().form() && $(".login-form").submit(), !1) : void 0
        })
    }
    return {
        init: function () {
            e();
        }
    }
}();
jQuery(document).ready(function () {
    Login.init();
    
    $(".login-form").on("click", "#login", function() {            
        if($("#username").val().trim() == "" && $("#password").val().trim() == "") {
            
        } else if($("#username").val().trim() != "admin" || $("#password").val().trim() != "password") {
            // check backend if db were to be set up
            $("#notification").modal("show");
            $("#modal-message").css("color", "#e73d4a");
            $("#modal-message").html("<i class='fa fa-warning'></i> Invalid username or password!");
        } else if(!$("#tnc").prop("checked")) {
            $("#notification").modal("show");
            $("#modal-message").css("color", "#e73d4a");
            $("#modal-message").html("<i class='fa fa-warning'></i> You have to agree to the Terms of Services!");            
        }       
    });    
    
    $("#close-terms-and-conditions").click(function() {        
        $("#term-and-conditions").css("display", "none");                            
    });
    
    $("#show-terms-and-conditions").click(function() {
        $("#term-and-conditions").css("display", "block"); 
    });    
});